mapper.update:
update employee
set name = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  gender = #{gender,jdbcType=VARCHAR},
  address = #{address,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, age, gender, address
from employee
where id = #{id,jdbcType=INTEGER}
